/**
 * Simple OpenAPI Parser Example for NUS Students
 * This demonstrates basic parser functionality without complexity
 */
declare const SIMPLE_API_SPEC = "openapi: 3.1.0\ninfo:\n  title: NUS Student API\n  description: Simple API for NUS students to learn OpenAPI\n  version: 1.0.0\n\nservers:\n  - url: https://api.nus.edu.sg/v1\n\npaths:\n  /students:\n    get:\n      summary: Get all students\n      responses:\n        '200':\n          description: List of students\n          content:\n            application/json:\n              schema:\n                type: array\n                items:\n                  $ref: '#/components/schemas/Student'\n    post:\n      summary: Create a student\n      requestBody:\n        required: true\n        content:\n          application/json:\n            schema:\n              $ref: '#/components/schemas/StudentCreate'\n      responses:\n        '201':\n          description: Student created\n          content:\n            application/json:\n              schema:\n                $ref: '#/components/schemas/Student'\n\n  /students/{id}:\n    get:\n      summary: Get student by ID\n      parameters:\n        - name: id\n          in: path\n          required: true\n          schema:\n            type: string\n            example: 'A1234567B'\n      responses:\n        '200':\n          description: Student details\n          content:\n            application/json:\n              schema:\n                $ref: '#/components/schemas/Student'\n\ncomponents:\n  schemas:\n    Student:\n      type: object\n      required:\n        - id\n        - name\n        - email\n      properties:\n        id:\n          type: string\n          example: 'A1234567B'\n        name:\n          type: string\n          example: 'John Doe'\n        email:\n          type: string\n          format: email\n          example: 'john.doe@u.nus.edu'\n        faculty:\n          type: string\n          enum: [Engineering, Science, Arts, Business]\n          example: 'Engineering'\n\n    StudentCreate:\n      type: object\n      required:\n        - name\n        - email\n      properties:\n        name:\n          type: string\n          example: 'Jane Smith'\n        email:\n          type: string\n          format: email\n          example: 'jane.smith@u.nus.edu'\n        faculty:\n          type: string\n          enum: [Engineering, Science, Arts, Business]\n          example: 'Science'";
declare function runParserExample(): Promise<void>;
export { runParserExample, SIMPLE_API_SPEC };
